mapper.insert:
INSERT INTO QUESTION
(
      ANS_SEQ
	, USER_ID
	, QST_CONTENT
	, QST_OPINION
	, QST_ANS_RATE
	, CRE_DT
	, UPD_DT
)
VALUES
(
	  #{ansSeq}
	, #{userId}
	, #{qstContent}
	, #{qstOpinion}
	, #{qstAnsRate}
	, NOW()
	, NOW()
)

mapper.update:
UPDATE QUESTION
SET   QST_OPINION = #{qstOpinion}
	, QST_ANS_RATE = #{qstAnsRate}
	, UPD_DT = NOW()
WHERE   QST_SEQ = #{qstSeq}
	AND USER_ID = #{userId}
